mapper.update:
UPDATE RECOMMENDATION
SET
    AVERAGE_RATING = (
        SELECT NVL(AVG(STAR), 0)
        FROM RECOMMENDATION_COMMENT
        WHERE RECOMMENDATION_ID = #{recommendationId}
    ),
    RATING_COUNT = (
        SELECT COUNT(ID)
        FROM RECOMMENDATION_COMMENT
        WHERE RECOMMENDATION_ID = #{recommendationId}
    )
WHERE ID = #{recommendationId}
